import React from "react";
import AddIcon from "@material-ui/icons/Add";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import SignalCellularAltIcon from "@material-ui/icons/SignalCellularAlt";
import styled from "styled-components";
import InfoOutlinedIcon from "@material-ui/icons/InfoOutlined";
import CallIcon from "@material-ui/icons/Call";
import Sidebar_Channel from "./Sidebar_Channel";
import { Avatar } from "@material-ui/core";
import MicIcon from "@material-ui/icons/Mic";
import HeadsetIcon from "@material-ui/icons/Headset";
import SettingsIcon from "@material-ui/icons/Settings";

function Sidebar() {
  return (
    <Container>
      <Top>
        <h3>Clever Programmer</h3>
        <ExpandMoreIcon />
      </Top>
      <Channels>
        <Channels_Header>
          <Sidebar_Header>
            <ExpandMoreIcon />
            <h4>Text Channels</h4>
          </Sidebar_Header>
          <AddChannels />
        </Channels_Header>
        <div>
          <Sidebar_Channel />
          <Sidebar_Channel />
          <Sidebar_Channel />
          <Sidebar_Channel />
        </div>
      </Channels>
      <Voice>
        <VoiceIcon />
        <VoiceInfo>
          <h3>Voice Connected</h3>
          <p>Stream</p>
        </VoiceInfo>
        <Icons>
          <InfoOutlinedIcon />
          <CallIcon />
        </Icons>
      </Voice>
      <Profile>
        <Avatar src="data:image/jpeg;base64," />
        <ProfileInfo>
          <h3>@sunnyjames</h3>
          <p>#thisisMyId</p>
        </ProfileInfo>
        <ProfileIcons>
          <MicIcon />
          <HeadsetIcon />
          <SettingsIcon />
        </ProfileIcons>
      </Profile>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  flex: 0.25;
  height: 100vh;
  background-color: #2f3135;
`;
const Top = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  background-color: #2f3135;
  color: rgb(249, 249, 249);
  border-bottom: 3px solid #26282c;
`;

const Channels = styled.div`
  flex: 1;
`;

const AddChannels = styled(AddIcon)`
  cursor: pointer;
  &:hover {
    color: white;
  }
`;
const Channels_Header = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 10px;
  background-color: #2f3135;
  color: gray;
`;
const Sidebar_Header = styled.div`
  display: flex;
  align-items: center;
`;

const Voice = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: gray;
  background-color: #2f3135;
  padding: 10px;
  border-top: 1px solid gray;
`;
const VoiceIcon = styled(SignalCellularAltIcon)`
  font-size: large;
  color: #4fb185;
`;
const VoiceInfo = styled.div`
  padding: 10px;
  flex: 1;
  h3 {
    color: #4fb185;
    transform: translateY(13px);
  }
  p {
    font-size: smaller;
    padding-top: -2px;
  }

  .MuiSvgIcon {
    padding: 10px;
  }
`;
const Icons = styled.div``;
const Profile = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: gray;
  background-color: #2f3135;
  padding: 10px;
  border-top: 1px solid gray;
`;

const ProfileInfo = styled.div`
  padding: 10px;
  flex: 1;
  h3 {
    color: #4fb185;
    transform: translateY(13px);
  }
  .MuiSvgIcon {
    padding: 10px;
  }
`;

const ProfileIcons = styled.div``;
export default Sidebar;
